// panics if option is empty
func (o OptionalNum) Head() Num {
	return o.Get()
}

func (o OptionalNum) Get() Num {
	if o.IsEmpty() {
		panic("Attempt to access non-existent value")
	}
	return *o.x
}

func (o OptionalNum) GetOrElse(d func() Num) Num {
	if o.IsEmpty() {
		return d()
	}
	return *o.x
}

func (o OptionalNum) OrElse(alternative func() OptionalNum) OptionalNum {
	if o.IsEmpty() {
		return alternative()
	}
	return o
}

