func (o OptionalNum) Find(predicate func(Num) bool) OptionalNum {
	if o.IsEmpty() {
		return o
	}
	if predicate(*o.x) {
		return o
	}
	return noneNum
}

func (o OptionalNum) Exists(predicate func(Num) bool) bool {
	if o.IsEmpty() {
		return false
	}
	return predicate(*o.x)
}

func (o OptionalNum) Forall(predicate func(Num) bool) bool {
	if o.IsEmpty() {
		return true
	}
	return predicate(*o.x)
}

func (o OptionalNum) Foreach(fn func(Num)) {
	if o.NonEmpty() {
		fn(*o.x)
	}
}

// Send gets a channel that will send all the elements in order.
func (o OptionalNum) Send() <-chan Num {
	ch := make(chan Num)
	go func() {
		if o.NonEmpty() {
			ch <- *o.x
		}
		close(ch)
	}()
	return ch
}

func (o OptionalNum) Filter(predicate func(Num) bool) NumCollection {
	return o.Find(predicate)
}

func (o OptionalNum) Partition(predicate func(Num) bool) (NumCollection, NumCollection) {
	if o.IsEmpty() {
		return o, o
	}
	if predicate(*o.x) {
		return o, noneNum
	}
	return noneNum, o
}

func (o OptionalNum) ToSlice() []Num {
	slice := make([]Num, o.Size())
	if o.NonEmpty() {
		slice[0] = *o.x
	}
	return slice
}

// ToInts gets all the elements in a []int.
func (o OptionalNum) ToInts() []int {
	slice := make([]int, o.Size())
	if o.NonEmpty() {
		slice[0] = int(*o.x)
	}
	return slice
}

// ToList gets the option's element in a NumList.
func (o OptionalNum) ToList() NumList {
	return NumList(o.ToSlice())
}

// ToSet gets the option's element in a NumSet.
func (o OptionalNum) ToSet() NumSet {
	return NewNumSet(o.ToSlice()...)
}

//-------------------------------------------------------------------------------------------------
// These methods require Num be comparable.

// Equals verifies that one or more elements of NumList return true for the passed func.
func (o OptionalNum) Equals(other NumCollection) bool {
	if o.IsEmpty() {
		return other.IsEmpty()
	}
	if other.IsEmpty() || other.Size() > 1 {
		return false
	}
	a := o.x
	s := other.ToSlice()
	b := s[0]
	return *a == b
}

func (o OptionalNum) Contains(value Num) bool {
	if o.IsEmpty() {
		return false
	}
	return *(o.x) == value
}

func (o OptionalNum) Count(value Num) int {
	if o.Contains(value) {
		return 1
	}
	return 0
}

// Distinct returns a new NumCollection whose elements are all unique. For options, this simply returns the
// receiver.
// Omitted if Num is not comparable.
func (o OptionalNum) Distinct() NumCollection {
	return o
}

// Min returns the minimum value of NumList. In the case of multiple items being equally minimal,
// the first such element is returned. Panics if the collection is empty.
func (o OptionalNum) Min() Num {
	return o.Get()
}

// Max returns the maximum value of NumList. In the case of multiple items being equally maximal,
// the first such element is returned. Panics if the collection is empty.
func (o OptionalNum) Max() Num {
	return o.Get()
}

//-------------------------------------------------------------------------------------------------

// Sum sums Num elements.
// Omitted if Num is not numeric.
func (o OptionalNum) Sum() Num {

	if o.IsEmpty() {
		return 0
	}
	return *(o.x)

}

// Mean computes the arithmetic mean of all elements.
// Panics if the list is empty.
func (o OptionalNum) Mean() float64 {
	if o.IsEmpty() {
		panic("Cannot compute the arithmetic mean of zero-length OptionalNum")
	}
	return float64(*(o.x))
}

//-------------------------------------------------------------------------------------------------

// String implements the Stringer interface to render the option as an array of one element.
func (o OptionalNum) String() string {
	return o.MkString3("[", ",", "]")
}

// MkString concatenates the values as a string.
func (o OptionalNum) MkString(sep string) string {
	return o.MkString3("", sep, "")
}

// MkString3 concatenates the values as a string.
func (o OptionalNum) MkString3(pfx, mid, sfx string) string {
	if o.IsEmpty() {
		return fmt.Sprintf("%s%s", pfx, sfx)
	}
	return fmt.Sprintf("%s%v%s", pfx, *(o.x), sfx)
}

//-------------------------------------------------------------------------------------------------

// NumSet is a typesafe set of Num items.
// The implementation is based on an underyling Go map, which can be manipulated directly,
// but otherwise instances are essentially immutable.
// The set-agebra functions *Union*, *Intersection* and *Difference* allow new variants to be constructed
// easily; these methods do not modify the input sets.
type NumSet map[Num]struct{}

//-------------------------------------------------------------------------------------------------

// NewNumSet constructs a new set containing the supplied values, if any.
func NewNumSet(values ...Num) NumSet {
	set := make(map[Num]struct{})
	for _, v := range values {
		set[v] = struct{}{}
	}
	return NumSet(set)
}

// BuildNumSetFrom constructs a new NumSet from a channel that supplies a stream of values
// until it is closed. The function returns all these values in a set (i.e. without any duplicates).
func BuildNumSetFrom(source <-chan Num) NumSet {
	set := make(map[Num]struct{})
	for v := range source {
		set[v] = struct{}{}
	}
	return NumSet(set)
}

//-------------------------------------------------------------------------------------------------

// IsSequence returns false for sets.
func (set NumSet) IsSequence() bool {
	return false
}

// IsSet returns true for sets.
func (set NumSet) IsSet() bool {
	return true
}

func (set NumSet) Size() int {
	return len(set)
}

func (set NumSet) IsEmpty() bool {
	return len(set) == 0
}

func (set NumSet) NonEmpty() bool {
	return len(set) > 0
}

// Head gets an arbitrary element.
func (set NumSet) Head() Num {
	for v := range set {
		return v
	}
	panic("Set is empty")
}

// ToSlice gets all the set's elements in a plain slice.
func (set NumSet) ToSlice() []Num {
	slice := make([]Num, set.Size())
	i := 0
	for v := range set {
		slice[i] = v
		i++
	}
	return slice
}

// ToInts gets all the elements in a []int.
func (set NumSet) ToInts() []int {
	slice := make([]int, len(set))
	i := 0
	for v := range set {
		slice[i] = int(v)
		i++
	}
	return slice
}

// ToList gets all the set's elements in a in SetList.
func (set NumSet) ToList() NumList {
	return NumList(set.ToSlice())
}

// ToSet gets the current set, which requires no further conversion.
func (set NumSet) ToSet() NumSet {
	return set
}

// Contains tests whether an item is already in the NumSet.
func (set NumSet) Contains(i Num) bool {
	_, found := set[i]
	return found
}

// ContainsAll tests whether many items are all in the NumSet.
func (set NumSet) ContainsAll(i ...Num) bool {
	for _, v := range i {
		if !set.Contains(v) {
			return false
		}
	}
	return true
}

func (set NumSet) actualSubset(other NumSet) bool {
	for item := range set {
		if !other.Contains(item) {
			return false
		}
	}
	return true
}

// Equals determines if two sets are equal to each other.
// They are considered equal if both are the same size and both have the same items.
func (set NumSet) Equals(other NumCollection) bool {
	otherSet, isSet := other.(NumSet)
	return isSet && set.Size() == other.Size() && set.actualSubset(otherSet)
}

// IsSubset determines if every item in the other set is in this set.
func (set NumSet) IsSubset(other NumSet) bool {
	return set.Size() <= other.Size() && set.actualSubset(other)
}

// IsProperSubset determines if every item in the other set is in this set and this set is
// smaller than the other.
func (set NumSet) IsProperSubset(other NumSet) bool {
	return set.Size() < other.Size() && set.actualSubset(other)
}

// IsSuperset determines if every item of this set is in the other set.
func (set NumSet) IsSuperset(other NumSet) bool {
	return other.IsSubset(set)
}

// Union returns a new set with all items in both sets.
func (set NumSet) Union(other NumSet) NumSet {
	union := NewNumSet()
	for item := range set {
		union[item] = struct{}{}
	}
	for item := range other {
		union[item] = struct{}{}
	}
	return union
}

// Intersection returns a new set with items that exist only in both sets.
func (set NumSet) Intersection(other NumSet) NumSet {
	intersection := NewNumSet()
	// loop over the smaller set
	if set.Size() < other.Size() {
		for item := range set {
			if other.Contains(item) {
				intersection[item] = struct{}{}
			}
		}
	} else {
		for item := range other {
			if set.Contains(item) {
				intersection[item] = struct{}{}
			}
		}
	}
	return intersection
}

// Difference returns a new set with items in the current set but not in the other set
func (set NumSet) Difference(other NumSet) NumSet {
	diffs := NewNumSet()
	for item := range set {
		if !other.Contains(item) {
			diffs[item] = struct{}{}
		}
	}
	return diffs
}

// Add creates a new set with elements added. This is similar to Union, but takes a slice of extra values.
// The receiver is not modified.
func (set NumSet) Add(others ...Num) NumSet {
	added := NewNumSet()
	for item := range set {
		added[item] = struct{}{}
	}
	for _, item := range others {
		added[item] = struct{}{}
	}
	return added
}

// Remove creates a new set with elements removed. This is similar to Difference, but takes a slice of unwanted values.
// The receiver is not modified.
func (set NumSet) Remove(unwanted ...Num) NumSet {
	removed := NewNumSet()
	for item := range set {
		removed[item] = struct{}{}
	}
	for _, item := range unwanted {
		delete(removed, item)
	}
	return removed
}

// Exists verifies that one or more elements of NumSet return true for the passed func.
func (set NumSet) Exists(fn func(Num) bool) bool {
	for v := range set {
		if fn(v) {
			return true
		}
	}
	return false
}

// Forall verifies that all elements of NumSet return true for the passed func.
func (set NumSet) Forall(fn func(Num) bool) bool {
	for v := range set {
		if !fn(v) {
			return false
		}
	}
	return true
}

// Foreach iterates over NumSet and executes the passed func against each element.
// The order of the elements is not well defined but is probably repeatably stable until the set is changed.
func (set NumSet) Foreach(fn func(Num)) {
	for v := range set {
		fn(v)
	}
}

// Send sends all elements along a channel of type Num.
// The order of the elements is not well defined but is probably repeatably stable until the set is changed.
func (set NumSet) Send() <-chan Num {
	ch := make(chan Num)
	go func() {
		for v := range set {
			ch <- v
		}
		close(ch)
	}()
	return ch
}

// Filter returns a new NumSet whose elements return true for func.
func (set NumSet) Filter(fn func(Num) bool) NumCollection {
	result := make(map[Num]struct{})
	for v := range set {
		if fn(v) {
			result[v] = struct{}{}
		}
	}
	return NumSet(result)
}

// Partition returns two new NumLists whose elements return true or false for the predicate, p.
// The first result consists of all elements that satisfy the predicate and the second result consists of
// all elements that don't. The relative order of the elements in the results is the same as in the
// original set.
func (set NumSet) Partition(p func(Num) bool) (NumCollection, NumCollection) {
	matching := make(map[Num]struct{})
	others := make(map[Num]struct{})
	for v := range set {
		if p(v) {
			matching[v] = struct{}{}
		} else {
			others[v] = struct{}{}
		}
	}
	return NumSet(matching), NumSet(others)
}

// CountBy gives the number elements of NumSet that return true for the passed predicate.
func (set NumSet) CountBy(predicate func(Num) bool) (result int) {
	for v := range set {
		if predicate(v) {
			result++
		}
	}
	return
}

// MinBy returns an element of NumSet containing the minimum value, when compared to other elements
// using a passed func defining ‘less’. In the case of multiple items being equally minimal, the first such
// element is returned. Panics if there are no elements.
func (set NumSet) MinBy(less func(Num, Num) bool) (result Num) {
	l := len(set)
	if l == 0 {
		panic("Cannot determine the minimum of an empty set.")
	}
	first := true
	for v := range set {
		if first {
			first = false
			result = v
		} else if less(v, result) {
			result = v
		}
	}
	return
}

// MaxBy returns an element of NumSet containing the maximum value, when compared to other elements
// using a passed func defining ‘less’. In the case of multiple items being equally maximal, the last such
// element is returned. Panics if there are no elements.
func (set NumSet) MaxBy(less func(Num, Num) bool) (result Num) {
	l := len(set)
	if l == 0 {
		panic("Cannot determine the maximum of an empty set.")
	}
	first := true
	for v := range set {
		if first {
			first = false
			result = v
		} else if less(result, v) {
			result = v
		}
	}
	return
}

// These methods require Num be numeric.

// Sum sums all elements in the set.
func (set NumSet) Sum() (result Num) {
	for v := range set {
		result += v
	}
	return
}

// Mean computes the arithmetic mean of all elements.
// Panics if the set is empty.
func (set NumSet) Mean() float64 {
	l := len(set)
	if l == 0 {
		panic("Cannot compute the arithmetic mean of zero-length NumSet")
	}
	sum := set.Sum()
	return float64(sum) / float64(l)
}

//-------------------------------------------------------------------------------------------------
// These methods require Num be ordered.

// Min returns the element with the minimum value. In the case of multiple items being equally minimal,
// any such element is returned. Panics if the collection is empty.
func (set NumSet) Min() (result Num) {
	if len(set) == 0 {
		panic("Cannot determine the minimum of an empty set.")
	}
	first := true
	for v := range set {
		if first {
			first = false
			result = v
		} else if v < result {
			result = v
		}
	}
	return
}

// Max returns the element with the maximum value. In the case of multiple items being equally maximal,
// any such element is returned. Panics if the collection is empty.
func (set NumSet) Max() (result Num) {
	if len(set) == 0 {
		panic("Cannot determine the maximum of an empty set.")
	}
	first := true
	for v := range set {
		if first {
			first = false
			result = v
		} else if v > result {
			result = v
		}
	}
	return
}

// String implements the Stringer interface to render the set as a comma-separated array.
func (set NumSet) String() string {
	return set.MkString3("[", ",", "]")
}

// MkString concatenates the values as a string.
func (set NumSet) MkString(sep string) string {
	return set.MkString3("", sep, "")
}

// MkString3 concatenates the values as a string.
func (set NumSet) MkString3(pfx, mid, sfx string) string {
	b := bytes.Buffer{}
	b.WriteString(pfx)
	l := len(set)
	if l > 0 {
		sep := ""
		for v := range set {
			b.WriteString(sep)
			b.WriteString(fmt.Sprintf("%v", v))
			sep = mid
		}
	}
	b.WriteString(sfx)
	return b.String()
}

//-------------------------------------------------------------------------------------------------

// This plumbing suite provides standard functions for piping data between goroutines.
// All these functions run until the input channel is closed (or all input channels are closed, if
// multiple). They then close their output channel(s).
// None of these functions create a goroutine - this must be done at the call site.

//-------------------------------------------------------------------------------------------------

// NumGenerator produces a stream of Num based on a supplied generator function.
// The function fn is invoked N times with the integers from 0 to N-1. Each result is sent out.
// Finally, the output channel is closed and the generator terminates.
//
// It is part of the Plumbing function suite for Num.
func NumGenerator(out chan<- Num, iterations int, fn func(int) Num) {
	NumGenerator3(out, 0, iterations-1, 1, fn)
}

// NumGenerator produces a stream of Num based on a supplied generator function.
// The function fn is invoked *(|to - from|) / |stride|* times with the integers in the range specified by
// from, to and stride. If stride is negative, from should be greater than to.
// For each iteration, the computed function result is sent out.
// If stride is zero, the loop never terminates. Otherwise, after the generator has reached the
// loop end, the output channel is closed and the generator terminates.
//
// It is part of the Plumbing function suite for Num.
func NumGenerator3(out chan<- Num, from, to, stride int, fn func(int) Num) {
	if (from > to && stride > 0) || (from < to && stride < 0) {
		panic("Loop conditions are divergent.")
	}
	if from > to && stride < 0 {
		for i := from; i >= to; i += stride {
			out <- fn(i)
		}
	} else {
		for i := from; i <= to; i += stride {
			out <- fn(i)
		}
	}
	close(out)
}

// NumDelta duplicates a stream of Num to two output channels.
// When the sender closes the input channel, both output channels are closed then the function terminates.
//
// It is part of the Plumbing function suite for Num.
func NumDelta(in <-chan Num, out1, out2 chan<- Num) {
	for v := range in {
		select {
		case out1 <- v:
			out2 <- v
		case out2 <- v:
			out1 <- v
		}
	}
	close(out1)
	close(out2)
}

// NumZip2 interleaves two streams of Num.
// Each input channel is used in turn, alternating between them.
// The function terminates when *both* input channels have been closed by their senders.
// The output channel is then closed also.
//
// It is part of the Plumbing function suite for Num.
func NumZip2(in1, in2 <-chan Num, out chan<- Num) {
	closed2 := false
	for v := range in1 {
		out <- v
		v, ok := <-in2
		if ok {
			out <- v
		} else {
			closed2 = true
		}
	}
	// need to drain in2 as well?
	if !closed2 {
		for _ = range in2 {
		}
	}
	close(out)
}

// NumMux2 multiplexes two streams of Num into a single output channel.
// Each input channel is used as soon as it is ready.
// When a signal is received from the closer channel, the output channel is then closed.
// Concurrently, both input channels are then passed into blackholes that comsume them until they too are closed,
// and the function terminates.
//
// It is part of the Plumbing function suite for Num.
func NumMux2(in1, in2 <-chan Num, closer <-chan bool, out chan<- Num) {
	running := true
	for running {
		select {
		case v := <-in1:
			out <- v
		case v := <-in2:
			out <- v
		case _ = <-closer:
			running = false
		}
	}
	go NumBlackHole(in1)
	go NumBlackHole(in2)
	close(out)
}

// NumBlackHole silently consumes a stream of Num.
// It terminates when the sender closes the channel.
//
// It is part of the Plumbing function suite for Num.
func NumBlackHole(in <-chan Num) {
	for _ = range in {
		// om nom nom
	}
}

// NumFilter filters a stream of Num, silently dropping elements that do not match the predicate p.
// When the sender closes the input channel, the output channel is closed then the function terminates.
//
// It is part of the Plumbing function suite for Num.
func NumFilter(in <-chan Num, out chan<- Num, p func(Num) bool) {
	for v := range in {
		if p(v) {
			out <- v
		}
	}
	close(out)
}

// NumPartition filters a stream of Num into two output streams using a predicate p, those that
// match and all others.
// When the sender closes the input channel, both output channels are closed then the function terminates.
//
// It is part of the Plumbing function suite for Num.
func NumPartition(in <-chan Num, matching, others chan<- Num, p func(Num) bool) {
	for v := range in {
		if p(v) {
			matching <- v
		} else {
			others <- v
		}
	}
	close(matching)
	close(others)
}

// NumMap transforms a stream of Num by applying a function fn to each item in the stream.
// When the sender closes the input channel, the output channel is closed then the function terminates.
//
// It is part of the Plumbing function suite for Num.
func NumMap(in <-chan Num, out chan<- Num, fn func(Num) Num) {
	for v := range in {
		out <- fn(v)
	}
	close(out)
}

// NumFlatMap transforms a stream of Num by applying a function fn to each item in the stream that
// gives zero or more results, all of which are sent out.
// When the sender closes the input channel, the output channel is closed then the function terminates.
//
// It is part of the Plumbing function suite for Num.
func NumFlatMap(in <-chan Num, out chan<- Num, fn func(Num) NumCollection) {
	for vi := range in {
		c := fn(vi)
		if c.NonEmpty() {
			for vo := range c.Send() {
				out <- vo
			}
		}
	}
	close(out)
}

//-------------------------------------------------------------------------------------------------
// List:MapTo[Foo]

// MapToFoo transforms NumList to FooList.
func (list NumList) MapToFoo(fn func(Num) Foo) FooCollection {
	result := make(FooList, 0, len(list))
	for _, v := range list {
		u := fn(v)
		result = append(result, u)
	}
	return result
}

// FlatMapToFoo transforms NumList to FooList, by repeatedly
// calling the supplied function and concatenating the results as a single flat list.
func (list NumList) FlatMapToFoo(fn func(Num) FooCollection) FooCollection {
	result := make(FooList, 0, len(list))
	for _, v := range list {
		u := fn(v)
		if u.NonEmpty() {
			result = append(result, (u.ToList())...)
		}
	}
	return result
}

//-------------------------------------------------------------------------------------------------
// List:With[Foo]

// FoldLeftFoo applies a binary operator to a start value and all elements of this list, going left to right.
func (list NumList) FoldLeftFoo(zero Foo, fn func(Foo, Num) Foo) Foo {
	sum := zero
	for _, v := range list {
		sum = fn(sum, v)
	}
	return sum
}

// FoldRightFoo applies a binary operator to a start value and all elements of this list, going right to left.
func (list NumList) FoldRightFoo(zero Foo, fn func(Foo, Num) Foo) Foo {
	sum := zero
	for i := len(list) - 1; i >= 0; i-- {
		sum = fn(sum, list[i])
	}
	return sum
}

// This methods require Num be comparable.

// GroupByFoo groups elements into a map keyed by Foo.
func (list NumList) GroupByFoo(fn func(Num) Foo) map[Foo]NumList {
	result := make(map[Foo]NumList)
	for _, v := range list {
		key := fn(v)
		result[key] = append(result[key], v)
	}
	return result
}

// These methods require Foo be ordered.

// MinByFoo finds the first element which yields the smallest value measured by function fn.
// fn is usually called a projection or measuring function.
// Panics if there are no elements.
func (list NumList) MinByFoo(fn func(Num) Foo) (result Num) {
	l := len(list)
	if l == 0 {
		panic("Cannot determine the minimum of an empty list.")
		return
	}
	result = list[0]
	if l > 1 {
		min := fn(result)
		for i := 1; i < l; i++ {
			v := list[i]
			f := fn(v)
			if min > f {
				min = f
				result = v
			}
		}
	}
	return
}

// MaxByFoo finds the first element which yields the largest value measured by function fn.
// fn is usually called a projection or measuring function.
// Panics if there are no elements.
func (list NumList) MaxByFoo(fn func(Num) Foo) (result Num) {
	l := len(list)
	if l == 0 {
		panic("Cannot determine the maximum of an empty list.")
	}
	result = list[0]
	if l > 1 {
		max := fn(result)
		for i := 1; i < l; i++ {
			v := list[i]
			f := fn(v)
			if max < f {
				max = f
				result = v
			}
		}
	}
	return
}

// List flags: {Collection:false List:true Option:true Set:true Plumbing:false Tag:map[Plumbing:true MapTo:true With:true]}
